        setPeople([]);
    }
function App() {
  return (
    <main>
      <section className="container">
        {/* Create a h3 element below*/}
        <h3>{data.length} birthdays today</h3>
		<List people={data} />
		<button onClick={clearAllRecords}>Clear All</button>
      </section>
    </main>
  );
}

export default App;
